// Exports club manager match report module

// Dependencies
var async = require('async');
var cmUtils = require('club_manager_utils');

function MatchReport(){}
var dbName = 'cm_match_report';

// Add custom object type
// cos = pencilblue custom object service
// util = pencilblue utilities
// cb = callback(error, boolean)
MatchReport.install = function(cos, util, cb) {
	// Custom object fields
	var matchReport = {
		name: dbName,
		fields: {
			name: { field_type: 'text' },
			date: { field_type: 'date' },
			description: { field_type: 'wysiwyg' },
			season: { field_type: 'peer_object', object_type: 'custom:cm_season' },
			players: { field_type: 'child_objects', object_type: 'custom:cm_player' }
		}
	};

	return cmUtils.createCustomObjectType(cos, util, dbName, matchReport, cb);
};

// Clear match report objects and remove custom object type
// cos = pencilblue custom object service
// util = pencilblue utilities
// cb = callback(error, boolean)
MatchReport.uninstall = function(cos, util, cb) {
	return cmUtils.removeCustomObjectType(cos, util, dbName, cb);
};

// Query all match reports from the database
// cos = pencilblue custom object service
// util = pencilblue utilities
// cb = callback(err, data)
MatchReport.getAll = function(cos, util, cb) {
	var selection = ['name', 'date', 'description', 'players'];
	cmUtils.queryCustomObjects(cos, util, dbName, selection, function(err, data) {
    // Fetch all players for each match report
    // So it's a 2-dimensional parallel fetch
    // For each report, fetch children.
    // Replace the old report with the report that includes the child objects.
    // After all queries are complete call the 'master' cb with the data.
    cmUtils.getCustomObjectTypeId(cos, util, dbName, function(err, typeId) {
      async.each(data, function(report, cb) {
        // Set fetch depth to 2: Includes player images (media objects).
        var options = { fetch_depth: 2 };
        cos.fetchChildren(report, options, typeId.toString(), function(err, object) {
          // If found, the report is overwritten with 
          // the object that includes the child objects.
          cb(err);
        }); 
      }, function(err) {
        cb(err, data);
      });
    });
  });
};

module.exports = MatchReport;
